import Link from "next/link";
import fetch from 'isomorphic-unfetch';
// import Header from "../components/Header";
// import Movies from '../components/Movies';

const Index = props => (
   <div>
      <h1>Pelix</h1>
      
      <ul>
        {props.movies.map(movie => (
            <li key={movie.movieId}>
                <Link href="/m/[id].js" as={`/m/${movie.movieId}`}>
                    <img src={movie.movieImage} alt={movie.movieName}/>
                </Link>
            </li>
        ))} 
      </ul>
   </div>
);

Index.getInitialProps = async function () {
  const res   = await fetch('http://api.tvmaze.com/shows');
  const data  = await res.json();

  console.log(`Fetched. Total: ${data.length}`);
  
  return {
      movies: {
        movieId       : data.map(mov => mov.id),
        movieImage    : data.map(mov => mov.image.medium),
        movieName     : data.map(mov => mov.name)
      }
  };
};

export default Index;
