import Link from "next/link";
import fetch from 'isomorphic-unfetch';
// import Header from "../components/Header";
// import Movies from '../components/Movies';

const Index = props => (
   <div>
      <h1>Pelix</h1>

      <ul>        
        {/* <li key={props.showId}>
            <Link href="/m/[id].js" as={`/m/${props.showId}`}>
                <a>{props.showImage.map(image => (
                    <img src={image.medium} alt={props.showName}/>
                ))}</a>
            </Link>
        </li> */}
        {props.showId.map(id => (
            <li key={id}>

            </li>
        ))}
      </ul>
   </div>
);

Index.getInitialProps = async function () {
  const res   = await fetch('http://api.tvmaze.com/shows');
  const data  = await res.json();

  console.log(`Fetched. Total: ${data.length}`);

  return {
      showId: data.map(entry => entry.id),
      showImage: data.map(entry => entry.image),
      showName: data.map(entry => entry.name)
  };
};

export default Index;
